                Categories.Add(item.Category);
            }
        }
        this.StateHasChanged();
    }

    public void Div_Click(int RowId)
    {
        if (BoolList.Contains(false))
        {
            for (int i = 0; i < BoolList.Count(); i++)
            {
                BoolList[i] = true;
            }
        }

        BoolList[RowId] = !BoolList[RowId];
        StateHasChanged();
    }

